const { Client } = require('pg')

const client = new Client({
    user: 'postgres',
    host: '127.0.0.1',
    database: 'postgres',
    password: 'mysecretpassword',
    port: 5432,
})

client.connect()

const query = 'SELECT * FROM role'

client.query(query)
    .then(res => {
        console.log(res.rows[0])
        client.end()
    })
    .catch(e => console.error(e.stack))

